import React, { useState } from "react";
import Form from "./Form";
import Todo from "./todo";

const App = () => {
  const [todos, setTodos] = useState([]);

  const addValue = (userValue) => {
    if (userValue) {
      const newItem = {
        id: Math.random().toString(36).substring(2.9),
        task: userValue
      }
      setTodos([...todos, newItem])
      console.log(newItem);
    }
  }

  return (
    <div className="App">
    <h1>commit: {todos.length}</h1>
    <Form 
    addValue={addValue}
    />
    {totos.map((todo) => {
      return(
        <Todo key={todo.id} todo={todo}/>
      )
    })}

    </div>
  )
}

export default App ;
