import React, {useState} from "react";

const Form = ({ addValue }) => {
  const [userTask, setUserTask] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault()
    addValue(handleSubmit)
    setUserTask("")
  }

return (
  <form onSubmit={handleSubmit}>
    <input 
    type="text"
    onChange={(e) => e.target.task}
    placeholder="ввидите что то"
    />  
  <button>создать</button>
  </form>
)
}

export default Form ;
