import React, { useState } from "react";
import Form from "./Form";
import Todo from "./todo";

const App = () => {
  const [todos, setTodos] = useState([])

  const addValue = (userValue) => {
    if (userValue) {
      const newValue = {
        id: Math.random().toString(36).substring(2.9),
        value: userValue
      }
      console.log(newValue);
      setTodos([...todos, newValue])
    }
  }



  return (
    <div className="App">
    <h1>у вас: {todos.length} коммитов</h1>
    <Form addValue={addValue}/>   
     {todos.map((todo) => {
       return (
         <Todo key={todos.id} todo={todo}/>
       )
     })}
    </div>
  );
}

export default App ;
