import React, { useState } from "react"; import useInput from "./hooks/useInput";

function App() {
    const inp = useInput ("hello world" , true)

  return (
    <div className="App">
    <form> 
      <input {...inp} />
    {inp.error && <span style={{color: "red"}}>{inp.error}</span>}
    </form>
    </div>
  );
}

export default App;
