import React, { useState } from "react"; import useInput from "./hooks/useInput";

function App() {
    const {value, onChange, onBlur, error} = useInput ("hello world" , true)
  return (
    <div className="App">
    <form> 
      <input 
      value={value}
      onChange={onchange}
      onBlur={onBlur}  
    />
    {error && <span style={{color: "red"}}>{error}</span>}
    </form>
    </div>
  );
}

export default App;
