import React, { useState } from "react";
import useInput from "./hooks/useInput";

function App() {
    const [value, onchange, onBlur, error] = useInput("hello world", true)
  return (
    <div className="App">
    <input
    value={value}
    onChange={onchange}
    onBlur={onBlur}
    />
    {error && <span style={{ color: "red" }}></span>}
    </div>
  );
}

export default App;
